        function convertsymtovar(KLterms,p_order)
            
            
            
            file=fopen('hpce35','w');
            [alpha,Psi_s,Psi_p,PsiSqNorm,P] = PC.Hermite_PC(KLterms,p_order);
            disp(length(Psi_s));
            for i=1:length(Psi_s)
                fprintf(file,'%s\n',char(Psi_s{i}));
            end
            fclose(file);
            
            file=fopen('hpce35','r');
            s=fgets(file);
            func='Psi={@(x)1, ';
            while ischar(s)
                s=(fgets(file));
                
                if ~ischar(s)
                    break;
                end
                str=strtrim(s);
                [a b] = regexp(str,'xi_?','match');
                k=length(b);
                for i=1:k
                    [a b] = regexp(str,'xi_?','match');
                    str= strcat(str(1:b(1)-1),'x(:,', str(b(1)+3),')',str(b(1)+4:end));
                end
                str= strrep(str,'*','.*');
                str= strrep(str,'^','.^');
                str= strrep(str,' ','');
                str=strcat('@(x)',str);
                %fprintf ('%s,',str);
                func=strcat(func, str ,',');
                
                
            end
            func=strcat(func(1:end-1),'};');
            disp(strcat('elseif(KLterms==',num2str(KLterms), ' && p_order==', num2str(p_order),')'));
            disp(func);
            fid=fopen('pcpoly.m','w');
            fwrite(fid,strcat('elseif(KLterms==',num2str(KLterms), ' && p_order==', num2str(p_order),')'));
            fprintf(fid,'\n');
            fwrite(fid,func);
            fclose(fid);
            disp('end');
            fclose(file);
        end
        
        
