        function [Psi_s,Psi_f,P] = Legendre_PC(M,p_order)
            
            %% Calculate the basis size of Psi
            P = 1;
            for s = 1:p_order
                P = P + (1/factorial(s))*prod(M+(0:s-1));   % Eq.(3.13) Ref.(2)
            end
            
            %% Calculate 1D Legendre polynomials: Recurrence relation
            % symbolic
            syms xi;
            Le_s    = cell(p_order,1);
            Le_s{1} = sym(1);
            Le_s{2} = xi;
            for j = 2:p_order+1
                Le_s{j+1} = expand(xi*Le_s{j}*(2*(j-1)+1) - (j-1)*Le_s{j-1})/(j);
            end
            % polynomial
            Le_p    = cell(p_order,1);
            Le_p{1} = 1;       % H_1 = 1
            Le_p{2} = [1 0];   % H_2 = x
            for n = 2:p_order+1
                Le_p{n+1} = [Le_p{n} 0] - (n-1)*[0 0 Le_p{n-1}];  % recursive formula
            end
            
            %% Define the number of RVs
            x   = cell(1,M);
            H_s = cell(p_order,M);   % Hermite polinomial for each dimension
            H_p = cell(p_order,M);   % Hermite polinomial for each dimension
            for j = 1:M
                x{j} = sym(sprintf('xi_%d',j));
                for i = 1:p_order+1
                    H_s{i,j} = subs(Le_s{i},xi,x{j});
                    H_p{i,j} = Le_p{i};
                end
            end
            
            %% M-dimensional PC computation
            Psi_s  = cell(P,1);   % symbolic version
            Psi_f  = cell(P,1);   % polynomial version
            alpha  = PC.multi_index(M,p_order);  % create the multi-index
            for i = 2:P+1
                mult_s = 1;
                mult_p = 1;
                for j = 1:M
                    mult_s = mult_s*H_s{alpha(i-1,j)+1,j};
                    % mult_p = conv(mult_p,H_p{alpha(i-1,j)+1,j});
                end
                Psi_s{i-1} = mult_s;
                %Psi_p{i-1} = mult_p;
            end
            for j=1:P
                str=char(Psi_s{j});
                
                
                [a b] = regexp(str,'xi_?','match');
                k=length(b);
                for i=1:k
                    [a b] = regexp(str,'xi_?','match');
                    str= strcat(str(1:b(1)-1),'x(:,', str(b(1)+3),')',str(b(1)+4:end));
                end
                str= replace(str,'*','.*');
                str= replace(str,'^','.^');
                str= replace(str,' ','');
                str=strcat('@(x)',str);
                Psi_f{j}= str2func(str);
            end
            
            return;
        end
        
