                Psi={@(x)1,@(x)x(:,1),@(x)x(:,2),@(x)x(:,3),@(x)x(:,4),@(x)x(:,5),@(x)x(:,6),@(x)x(:,1).^2-1,@(x)x(:,1).*x(:,2),@(x)x(:,1).*x(:,3),@(x)x(:,1).*x(:,4),@(x)x(:,1).*x(:,5),@(x)x(:,1).*x(:,6),@(x)x(:,2).^2-1,@(x)x(:,2).*x(:,3),@(x)x(:,2).*x(:,4),@(x)x(:,2).*x(:,5),@(x)x(:,2).*x(:,6),@(x)x(:,3).^2-1,@(x)x(:,3).*x(:,4),@(x)x(:,3).*x(:,5),@(x)x(:,3).*x(:,6),@(x)x(:,4).^2-1,@(x)x(:,4).*x(:,5),@(x)x(:,4).*x(:,6),@(x)x(:,5).^2-1,@(x)x(:,5).*x(:,6),@(x)x(:,6).^2-1};

            else
                
                disp([KLterms, p_order]);
                error('Not defined yet');
                
        end
    end
        
        function [alpha,Psi_s,Psi_p,PsiSqNorm,P] = Hermite_PC(M,p_order)
            
            %% Calculate the basis size of Psi
            P = 1;
            for s = 1:p_order
                P = P + (1/factorial(s))*prod(M+(0:s-1));   % Eq.(3.13) Ref.(2)
            end
            
            %% Calculate 1D Hermite polynomials: Recurrence relation
            % symbolic
            syms xi;
            He_s    = cell(p_order,1);
            He_s{1} = sym(1);
            He_s{2} = xi;
            for j = 2:p_order+1
                He_s{j+1} = expand(xi*He_s{j} - (j-1)*He_s{j-1});
            end
            % polynomial
            He_p    = cell(p_order,1);
            He_p{1} = 1;       % H_1 = 1
            He_p{2} = [1 0];   % H_2 = x
            for n = 2:p_order+1
                He_p{n+1} = [He_p{n} 0] - (n-1)*[0 0 He_p{n-1}];   % recursive formula
            end
            
            %% Define the number of RVs
            x   = cell(1,M);
            H_s = cell(p_order,M);   % Hermite polinomial for each dimension
            H_p = cell(p_order,M);   % Hermite polinomial for each dimension
            for j = 1:M
                x{j} = sym(sprintf('xi_%d',j));
                for i = 1:p_order+1
                    H_s{i,j} = subs(He_s{i},xi,x{j});
                    H_p{i,j} = He_p{i};
                end
            end
            
            %% M-dimensional PC computation
            Psi_s  = cell(P,1);   % symbolic version
            Psi_p  = cell(P,1);   % polynomial version
            alpha  = PC.multi_index(M,p_order);  % create the multi-index
            for i = 2:P+1
                mult_s = 1;
                mult_p = 1;
                for j = 1:M
                    mult_s = mult_s*H_s{alpha(i-1,j)+1,j};
                    mult_p = conv(mult_p,H_p{alpha(i-1,j)+1,j});
                end
                Psi_s{i-1} = mult_s;
                Psi_p{i-1} = mult_p;
            end
            
            %% Calculate the square norm
            PsiSqNorm  = prod(factorial(alpha),2);
            
            %% show results
            %fprintf('Number of random variables (K-L terms): M = %d \n',M);
            %fprintf('Order of the polynomial chaos: p = %d \n',p_order);
            %fprintf('Total number of polynomial chaos: p = %d \n\n',P);
            for k = 1:P
                %fprintf('j = %d \t',k-1);
                % fprintf('E[Psi^2] = %d \t\t',PsiSqNorm(k));
                %fprintf('Psi   = %s \n',char(Psi_s{k}));
            end
            
            return;
        end
        
        function c = c_ijk_L(M,p)
            
            
            %% procedure
            c = cell(M,1);   % allocation space
            [Psi,~,P] = PC.Legendre_PC(M,p);   % compute Hermite pols
            
            n=10;
            % ex=0;
            % for k=0 : n
            %     ex=ex+(-1)^k;
            % end
            %ex=ex/(n+1)
            syms xi_1 xi_2 xi_3 xi_4 xi_5 xi_6;
            syms varr;
            c=cell(M,1);
            
            for n=0:50
                if(rem(n,2)==0)
                    ex(n+1)=1/(n+1);
                else
                    ex(n+1)=0;
                end
            end
            
            varr={1, xi_1, xi_2, xi_3, xi_4,xi_5,xi_6};
            exn=1/(n+1)*(1+(-1)^n)/2;
            P=size(Psi,1);
            for i=1:M +1
                c{i}=zeros(P,P);
                for j=1:P
                    for k=1:P
                        psjk=expand(Psi{j}*Psi{k}*varr{i});
                        cl=children(psjk+100);
                        c{i}(j,k)=0;
                        for nc=1:length(cl)
                            s=symvar(cl(nc));
                            val=1;
                            for nv=1:length(s)
                                n=polynomialDegree(cl(nc),s(nv));
                                
                                val=val*ex(n+1)*coeffs(cl(nc));
                            end
                            if(isempty(s))
                                c{i}(j,k)=c{i}(j,k)+cl(nc)-100;
                            else
                                c{i}(j,k)=c{i}(j,k)+val;
                            end
                            
                        end
                    end
                end
            end
        end

        function [c, P] = c_ijk(M,p,method)
            
            
            %% procedure
            c = cell(M,1);   % allocation space
            [alpha,Psi,~,PsiSqNorm,P] = PC.Hermite_PC(M,p);   % compute Hermite pols
             
            switch method;
                case 1;   %% Sudret's method
                    for i = 1:M
                        c{i} = sparse(P,P);
                    end
                    c{1} = diag(PsiSqNorm);
                    
                    for j = 1:P
                        alpha_p = alpha(j,:);
                        for k = 1:P
                            alpha_q = alpha(k,:);
                            %disp(alpha_p)
                            %disp(alpha_q)
                            for i = 1:M
                                idx = [1:(i-1) (i+1):M];
                                 
                                % Test if alpha_p and alpha_q differ only by i-th coeff
                                if isequal(alpha_p(idx),alpha_q(idx))
                                    c{i+1}(j,k) = (factorial(alpha_p(i))*(alpha_p(i)-1==alpha_q(i)) ...
                                        + factorial(alpha_q(i))*(alpha_p(i)==alpha_q(i)-1)) ...
                                        *(PsiSqNorm(j)/factorial(alpha_p(i)));
                                    %disp([alpha_p(i)-1, alpha_q(i)])
                                end
                            end
                        end
                    end
                    
                    
                case 2;   %% symbolic integration method
                    % Define the number of RVs in sym
                    x = cell(1,M);
                    for j = 1:M
                        x{j} = sym(sprintf('xi_%d',j));
                    end
                    %
                    dw   = @(x) exp(-0.5*x^2)/sqrt(2*pi);  %% Gaussian measure
                    c{1} = diag(PsiSqNorm);
                    
                    for i = 1:M
                        c{i+1} = sparse(P,P);
                        for j = 1:P
                            for k = 1:P
                                msg  = fprintf('C_{%d,%d,%d}',i,j,k);
                                cijk = x{i}*Psi{j}*Psi{k};
                                for l = 1:M
                                    cijk = int(cijk*dw(x{l}),x{l},-inf,inf);
                                    if cijk == 0
                                        break;
                                    end
                                end
                                c{i+1}(j,k) = double(cijk);
                                fprintf(repmat('\b',1,msg));
                            end
                        end
                    end
                    
                otherwise;
                    error('The argument -method- must be 1 (Sudret) or 2 (symbolic)');
            end
            %
            % %% plot the c_ijk matrices
            % set(0,'defaultTextInterpreter','latex');
            % figure;
            % for i = 1:M+1
            %    subplot(1,M+1,i);
            %    nz = nnz(c{i}); spy(c{i},'b.',8);
            %    set(gca,'FontSize',18);
            %    title(sprintf('$*nnz=%g~*i=%g$',nz,i-1),'FontSize',20);   xlabel('');
            % end
            
            return;
            
            
        end
        
        function alpha = multi_index(M,p)
            %% multi-index sequence for the computation of M-dimensional polynomials
            %{
    --------------------------------------------------------------------------
    Created by:                       Date:           Comment:
    Felipe Uribe                      Oct/2014        ---
    furibec@unal.edu.co
    Universidad Nacional de Colombia
    Manizales Campus
    --------------------------------------------------------------------------
    *Input:
     M           % number of K-L terms (number of random variables)
     p_order     % order of PC
    --------------------------------------------------------------------------
    *Output:
     alpha       % multi-index sequence
    --------------------------------------------------------------------------
    Based on:
    1."Numerical methods for stochastic computations. A spectral method approach"
       D. Xiu. 2010. Princeton University Press.
    2."Stochastic finite element methods and reliability"
       B. Sudret and A. Der Kiureghian. State of the art report.
    --------------------------------------------------------------------------
            %}
            
            %% procedure
            alpha    = cell(p+1,1);    % multi-index
            alpha{1} = zeros(1,M);     % multi-index for length 0
            
            switch M;
                case 1;  % dimension = 1
                    for q = 1:p
                        alpha{q+1} = q;
                    end
                otherwise;  % dimension>1
                    for q = 1:p
                        s          = nchoosek(1:M+q-1,M-1);
                        s1         = zeros(size(s,1),1);
                        s2         = (M+q)+s1;
                        %disp(s)
                        alpha{q+1} = flipud(diff([s1 s s2],1,2))-1;   % -1 due to MATLAB indexing
                        if sum(alpha{q+1},2) ~= q*ones(nchoosek(M+q-1,M-1),1)
                            error('The sum of each row has to be equal to q-th order');
                        end
                    end
            end
            alpha = cell2mat(alpha);   % as in Ref.(1)
            % alpha = flipud(alpha')';   % ~as in Ref.(2)
            
            return;
        end
    end
